import React, { useEffect, useState } from 'react';
import API from '../api';

function ProductManagement() {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({ title: '', price: 0, discount_percentage: 0, discounted_price: 0, quantity: 0, product_id: 0 });

  const fetchProducts = () => API.get("/products").then(res => setProducts(res.data));
  useEffect(() => { fetchProducts(); }, []);

  const handleAdd = () => {
    API.post("/products", newProduct).then(() => fetchProducts());
  };

  const handleDelete = (id) => {
    API.delete(`/products/${id}`).then(() => fetchProducts());
  };

  return (
    <div>
      <h1>Product Management</h1>
      <input placeholder="Title" onChange={e => setNewProduct({...newProduct, title:e.target.value})} />
      <button onClick={handleAdd}>Add Product</button>
      <ul>
        {products.map(p => (
          <li key={p.id}>{p.title} - ${p.price} 
            <button onClick={() => handleDelete(p.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ProductManagement;
