import React, { useEffect, useState } from "react";
import API from "../api/api";

function ProductManagement() {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({ title: "", price: "" });
  const [editingProduct, setEditingProduct] = useState(null);

  useEffect(() => {
    loadProducts();
  }, []);

  const loadProducts = async () => {
    try {
      const res = await API.get("/products");
      setProducts(res.data);
    } catch (err) {
      console.error(err);
      alert("Failed to fetch products");
    }
  };

  const handleAddProduct = async () => {
    if (!newProduct.title || !newProduct.price) {
      alert("Enter both title and price");
      return;
    }
    try {
      await API.post("/products", {
        title: newProduct.title,
        price: parseFloat(newProduct.price),
      });
      setNewProduct({ title: "", price: "" });
      loadProducts();
    } catch (err) {
      console.error(err);
      alert("Failed to add product");
    }
  };

  const handleEditProduct = (product) => {
    setEditingProduct(product);
    setNewProduct({ title: product.title, price: product.price });
  };

  const handleUpdateProduct = async () => {
    try {
      await API.put(`/products/${editingProduct.id}`, {
        title: newProduct.title,
        price: parseFloat(newProduct.price),
      });
      setEditingProduct(null);
      setNewProduct({ title: "", price: "" });
      loadProducts();
    } catch (err) {
      console.error(err);
      alert("Failed to update product");
    }
  };

  const handleDeleteProduct = async (id) => {
    if (!window.confirm("Are you sure you want to delete this product?")) return;
    try {
      await API.delete(`/products/${id}`);
      loadProducts();
    } catch (err) {
      console.error(err);
      alert("Failed to delete product");
    }
  };

  return (
    <div style={{ padding: 30 }}>
      <h2>Product Management</h2>
      <div style={{ marginBottom: 20 }}>
        <input
          placeholder="Product Title"
          value={newProduct.title}
          onChange={(e) => setNewProduct({ ...newProduct, title: e.target.value })}
        />
        <input
          placeholder="Price"
          type="number"
          value={newProduct.price}
          onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
        />
        {editingProduct ? (
          <button onClick={handleUpdateProduct}>Update Product</button>
        ) : (
          <button onClick={handleAddProduct}>Add Product</button>
        )}
      </div>

      <ul>
        {products.map((p) => (
          <li key={p.id} style={{ marginBottom: 10 }}>
            {p.title} - ${p.price.toFixed(2)}
            <button onClick={() => handleEditProduct(p)} style={{ marginLeft: 10 }}>
              Edit
            </button>
            <button onClick={() => handleDeleteProduct(p.id)} style={{ marginLeft: 5 }}>
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ProductManagement;
