import React, { useEffect, useState } from "react";
import API from "../api";

function Dashboard() {
  const [analytics, setAnalytics] = useState({});
  const [loading, setLoading] = useState(false);

  // Load analytics from backend
  const loadAnalytics = async () => {
    try {
      const res = await API.get("/analytics");
      setAnalytics(res.data);
    } catch (err) {
      console.error("Analytics error:", err.response ? err.response.data : err);
    }
  };

  // Fetch products from external API and save to DB
  const handleFetchCarts = async () => {
    setLoading(true);
    try {
      await API.post("/fetch-carts");
      alert("Products imported successfully!");
      loadAnalytics(); // refresh analytics
    } catch (err) {
      console.error("Fetch carts error:", err.response ? err.response.data : err);
      alert("Failed to import products. Check console for details.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadAnalytics(); // load analytics on page load
  }, []);

  // Simple card component
  const Card = ({ title, value }) => (
    <div
      style={{
        border: "1px solid #ddd",
        borderRadius: 8,
        padding: 15,
        margin: 10,
        flex: 1,
        minWidth: 200,
        boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
        background: "#fff",
      }}
    >
      <h4 style={{ margin: "0 0 10px 0" }}>{title}</h4>
      <p style={{ fontSize: 18, fontWeight: "bold", margin: 0 }}>
        {typeof value === "number" ? value.toLocaleString() : value}
      </p>
    </div>
  );

  return (
    <div style={{ padding: 20, fontFamily: "Arial, sans-serif", background: "#f4f6f8", minHeight: "100vh" }}>
      <h1 style={{ color: "#333" }}>Analytics Dashboard</h1>

      <button
        onClick={handleFetchCarts}
        disabled={loading}
        style={{
          padding: "10px 20px",
          fontSize: 16,
          cursor: loading ? "not-allowed" : "pointer",
          borderRadius: 6,
          border: "none",
          background: "#007bff",
          color: "#fff",
          marginBottom: 20,
        }}
      >
        {loading ? "Importing..." : "Import Products"}
      </button>

      {analytics && Object.keys(analytics).length > 0 ? (
        <div style={{ display: "flex", flexWrap: "wrap" }}>
          <Card title="Total Before Discount" value={`$${analytics.total_before_discount}`} />
          <Card title="Total After Discount" value={`$${analytics.total_after_discount}`} />
          <Card title="Average Discount (%)" value={analytics.avg_discount_percentage} />
          <Card title="Most Expensive Product" value={analytics.most_expensive_product} />
          <Card title="Cheapest Product" value={analytics.cheapest_product} />
          <Card title="Highest Discount Product" value={analytics.highest_discount_product} />
          <Card title="Total Quantity" value={analytics.total_quantity} />
          <Card title="Unique Products" value={analytics.unique_products} />
        </div>
      ) : (
        <p>Loading analytics...</p>
      )}
    </div>
  );
}

export default Dashboard;
