import React from 'react';
import { GoogleLogin } from '@react-oauth/google';
import API from '../api';
import { useNavigate } from "react-router-dom";

function LoginPage() {
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    const res = await API.post("/login", { username: "admin", password: "password" });
    localStorage.setItem("jwt", res.data.access_token);
    alert("Logged in!");
    navigate("/dashboard");   // <<< ADD REDIRECT
  };

  const handleGoogle = async (credentialResponse) => {
    const res = await API.post("/google-login", { token: credentialResponse.credential });
    localStorage.setItem("jwt", res.data.access_token);
    alert("Google login success!");
    navigate("/dashboard");    // <<< THIS WAS MISSING
  };

  return (
    <div>
      <h1>Login</h1>
      <form onSubmit={handleLogin}>
        <button type="submit">Login as Admin</button>
      </form>
      <GoogleLogin onSuccess={handleGoogle} onError={() => console.log("Login failed")} />
    </div>
  );
}

export default LoginPage;
