import React, { useState, useEffect } from 'react';
import API from '../api/api';

function ProductManagement() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [newProduct, setNewProduct] = useState({ title: '', price: '' });

  useEffect(() => {
    loadProducts();
  }, []);

  const loadProducts = async () => {
    setLoading(true);
    try {
      const res = await API.get('/products'); // replace with your API endpoint
      setProducts(res.data);
    } catch (err) {
      console.error(err);
      alert('Failed to fetch products.');
    } finally {
      setLoading(false);
    }
  };

  const handleAddProduct = async () => {
    if (!newProduct.title || !newProduct.price) {
      alert('Enter title and price.');
      return;
    }
    try {
      await API.post('/products', newProduct); // API POST endpoint
      alert('Product added successfully!');
      setNewProduct({ title: '', price: '' });
      loadProducts();
    } catch (err) {
      console.error(err);
      alert('Failed to add product.');
    }
  };

  const handleDeleteProduct = async (id) => {
    if (!window.confirm('Are you sure you want to delete this product?')) return;
    try {
      await API.delete(`/products/${id}`);
      alert('Product deleted!');
      loadProducts();
    } catch (err) {
      console.error(err);
      alert('Failed to delete product.');
    }
  };

  return (
    <div style={{ padding: '30px' }}>
      <h1>Product Management</h1>

      {/* Add new product */}
      <div style={{ marginBottom: '20px' }}>
        <input
          placeholder="Title"
          value={newProduct.title}
          onChange={(e) => setNewProduct({ ...newProduct, title: e.target.value })}
        />
        <input
          placeholder="Price"
          type="number"
          value={newProduct.price}
          onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
        />
        <button onClick={handleAddProduct}>Add Product</button>
      </div>

      {/* Product list */}
      {loading ? (
        <p>Loading products...</p>
      ) : (
        <table border="1" cellPadding="10" cellSpacing="0">
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Price</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {products.map((p) => (
              <tr key={p.id}>
                <td>{p.id}</td>
                <td>{p.title}</td>
                <td>${p.price}</td>
                <td>
                  <button onClick={() => handleDeleteProduct(p.id)}>Delete</button>
                  {/* You can add an Edit button here later */}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default ProductManagement;
