import React, { useEffect, useState } from "react";
import API from "../api/api";

function ProductManagement() {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({ title: "", price: "" });
  const [editingProduct, setEditingProduct] = useState(null);

  useEffect(() => {
    loadProducts();
  }, []);

  const loadProducts = async () => {
    try {
      const res = await API.get("/products");
      setProducts(res.data);
    } catch (err) {
      console.error(err);
      alert("Failed to fetch products");
    }
  };

  const handleAddOrUpdate = async () => {
    if (!newProduct.title || newProduct.price === "") {
      alert("Please enter both title and price");
      return;
    }

    try {
      if (editingProduct) {
        await API.put(`/products/${editingProduct.id}`, {
          title: newProduct.title,
          price: parseFloat(newProduct.price),
        });
        setEditingProduct(null);
      } else {
        await API.post("/products", {
          title: newProduct.title,
          price: parseFloat(newProduct.price),
        });
      }
      setNewProduct({ title: "", price: "" });
      loadProducts();
    } catch (err) {
      console.error(err);
      alert("Failed to add/update product");
    }
  };

  const handleEdit = (product) => {
    setEditingProduct(product);
    setNewProduct({ title: product.title, price: product.price });
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure?")) return;
    try {
      await API.delete(`/products/${id}`);
      loadProducts();
    } catch (err) {
      console.error(err);
      alert("Failed to delete product");
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Product Management</h2>

      {/* Form */}
      <div style={styles.form}>
        <input
          placeholder="Product Title"
          value={newProduct.title}
          onChange={(e) => setNewProduct({ ...newProduct, title: e.target.value })}
          style={styles.input}
        />
        <input
          placeholder="Price"
          type="number"
          value={newProduct.price}
          onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
          style={styles.input}
        />
        <button style={styles.addButton} onClick={handleAddOrUpdate}>
          {editingProduct ? "Update Product" : "Add Product"}
        </button>
      </div>

      {/* Products Table */}
      <table style={styles.table}>
        <thead>
          <tr style={styles.tableHeader}>
            <th>ID</th>
            <th>Title</th>
            <th>Price</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.map((p) => (
            <tr key={p.id} style={styles.tableRow}>
              <td>{p.id}</td>
              <td>{p.title}</td>
              <td>${p.price.toFixed(2)}</td>
              <td>
                <button style={styles.editButton} onClick={() => handleEdit(p)}>
                  Edit
                </button>
                <button style={styles.deleteButton} onClick={() => handleDelete(p.id)}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

// Styles
const styles = {
  container: {
    padding: 30,
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    background: "#f7f9fc",
    minHeight: "100vh",
  },
  heading: {
    textAlign: "center",
    marginBottom: 20,
    fontSize: "2rem",
    color: "#333",
    letterSpacing: "1px",
  },
  form: {
    display: "flex",
    justifyContent: "center",
    marginBottom: 30,
    gap: 10,
    flexWrap: "wrap",
  },
  input: {
    padding: 10,
    fontSize: 16,
    borderRadius: 8,
    border: "1px solid #ccc",
    width: 200,
  },
  addButton: {
    padding: "10px 20px",
    background: "linear-gradient(135deg, #6a11cb 0%, #2575fc 100%)",
    color: "white",
    fontWeight: "bold",
    border: "none",
    borderRadius: 8,
    cursor: "pointer",
    transition: "transform 0.2s, box-shadow 0.2s",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    background: "#fff",
    borderRadius: 12,
    overflow: "hidden",
    boxShadow: "0 10px 20px rgba(0,0,0,0.1)",
  },
  tableHeader: {
    background: "#6a11cb",
    color: "white",
  },
  tableRow: {
    transition: "background 0.2s",
  },
  editButton: {
    padding: "5px 10px",
    marginRight: 5,
    border: "none",
    borderRadius: 5,
    background: "#2575fc",
    color: "#fff",
    cursor: "pointer",
  },
  deleteButton: {
    padding: "5px 10px",
    border: "none",
    borderRadius: 5,
    background: "#fc5c5c",
    color: "#fff",
    cursor: "pointer",
  },
};

// Hover effects
styles.addButton[":hover"] = { transform: "translateY(-2px)", boxShadow: "0px 5px 15px rgba(0,0,0,0.3)" };

export default ProductManagement;
