import React, { useEffect, useState } from "react";
import API from "../api";

function Dashboard() {
  const [analytics, setAnalytics] = useState({});
  const [loading, setLoading] = useState(false);

  // Load analytics
  const loadAnalytics = async () => {
    try {
      const res = await API.get("/analytics");
      setAnalytics(res.data);
    } catch (err) {
      console.error("Analytics error:", err.response ? err.response.data : err);
    }
  };

  // Fetch products
  const handleFetchCarts = async () => {
    setLoading(true);
    try {
      await API.post("/fetch-carts");
      alert("Products imported successfully!");
      loadAnalytics();
    } catch (err) {
      console.error("Fetch carts error:", err.response ? err.response.data : err);
      alert("Failed to import products. Check console.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadAnalytics();
  }, []);

  // Helper: normalize value for bar width
  const getBarWidth = (value, max) => `${Math.min((value / max) * 100, 100)}%`;

  // Find max for bar scaling
  const numericValues = [
    analytics.total_before_discount,
    analytics.total_after_discount,
    analytics.avg_discount_percentage,
    analytics.total_quantity,
    analytics.unique_products,
  ].filter(Boolean);
  const maxValue = Math.max(...numericValues, 1);

  return (
    <div
      style={{
        padding: 20,
        fontFamily: "Arial, sans-serif",
        background: "#f0f4f8",
        minHeight: "100vh",
      }}
    >
      <h1 style={{ color: "#333", marginBottom: 20 }}>Analytics Dashboard</h1>

      <button
        onClick={handleFetchCarts}
        disabled={loading}
        style={{
          padding: "10px 20px",
          fontSize: 16,
          cursor: loading ? "not-allowed" : "pointer",
          borderRadius: 6,
          border: "none",
          background: "#007bff",
          color: "#fff",
          marginBottom: 30,
        }}
      >
        {loading ? "Importing..." : "Import Products"}
      </button>

      {analytics && Object.keys(analytics).length > 0 ? (
        <div style={{ display: "flex", flexWrap: "wrap", gap: 20 }}>
          {Object.entries(analytics).map(([key, value]) => {
            const isNumeric =
              typeof value === "number" && key !== "avg_discount_percentage";
            return (
              <div
                key={key}
                style={{
                  flex: "1 1 250px",
                  background: "#fff",
                  borderRadius: 8,
                  padding: 15,
                  boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
                }}
              >
                <h4 style={{ margin: "0 0 10px 0", textTransform: "capitalize" }}>
                  {key.replace(/_/g, " ")}
                </h4>
                <p style={{ fontWeight: "bold", fontSize: 16 }}>
                  {typeof value === "number" ? value.toLocaleString() : value}
                </p>
                {isNumeric && (
                  <div
                    style={{
                      height: 10,
                      width: "100%",
                      background: "#e0e0e0",
                      borderRadius: 5,
                      marginTop: 8,
                    }}
                  >
                    <div
                      style={{
                        height: "100%",
                        width: getBarWidth(value, maxValue),
                        background: "#007bff",
                        borderRadius: 5,
                        transition: "width 0.5s",
                      }}
                    />
                  </div>
                )}
              </div>
            );
          })}
        </div>
      ) : (
        <p>Loading analytics...</p>
      )}
    </div>
  );
}

export default Dashboard;
