import API from "../api";
import { useNavigate } from "react-router-dom";

const navigate = useNavigate();

async function handleGoogleLoginSuccess(response) {
  const token = response.credential;

  try {
    const res = await API.post("/google-login", { token });

    if (res.data.status === "success") {
      alert("Google login success!");
      localStorage.setItem("jwt", res.data.jwt); // optional
      navigate("/dashboard"); // <-- THIS WAS MISSING
    } else {
      alert("Login failed!");
    }
  } catch (err) {
    console.error(err);
    alert("Server error");
  }
}
