import React, { useEffect, useState } from 'react';
import API from '../api';

function Dashboard() {
  const [analytics, setAnalytics] = useState({});
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    loadAnalytics();
  }, []);

  const loadAnalytics = async () => {
    try {
      const res = await API.get('/analytics');
      setAnalytics(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleFetchCarts = async () => {
    setLoading(true);
    try {
      await API.post('/fetch-carts');
      alert('Products imported successfully!');
      loadAnalytics();
    } catch (err) {
      console.error(err);
      alert('Failed to import products. Check console for details.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      <h1>Analytics Dashboard</h1>
      <button style={styles.button} onClick={handleFetchCarts} disabled={loading}>
        {loading ? 'Importing...' : 'Import Products'}
      </button>

      <div style={styles.cardsContainer}>
        {analytics && Object.keys(analytics).length > 0 && (
          <>
            <div style={styles.card}>Total Before Discount: ${analytics.total_before_discount}</div>
            <div style={styles.card}>Total After Discount: ${analytics.total_after_discount}</div>
            <div style={styles.card}>Average Discount: {analytics.avg_discount_percentage}%</div>
            <div style={styles.card}>Most Expensive: {analytics.most_expensive_product}</div>
            <div style={styles.card}>Cheapest: {analytics.cheapest_product}</div>
            <div style={styles.card}>Highest Discount: {analytics.highest_discount_product}</div>
            <div style={styles.card}>Total Quantity: {analytics.total_quantity}</div>
            <div style={styles.card}>Unique Products: {analytics.unique_products}</div>
          </>
        )}
      </div>
    </div>
  );
}

const styles = {
  container: {
    padding: 20,
    fontFamily: 'Arial, sans-serif'
  },
  button: {
    padding: '10px 20px',
    margin: '10px 0',
    cursor: 'pointer',
    background: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: 5
  },
  cardsContainer: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
    gap: 20,
    marginTop: 20
  },
  card: {
    background: '#f0f0f0',
    padding: 15,
    borderRadius: 8,
    boxShadow: '0px 2px 5px rgba(0,0,0,0.1)',
    fontWeight: 'bold'
  }
};

export default Dashboard;
